mapper.update:
UPDATE SYS_L_ACTION_TRACK
NOTES = #{ notes,jdbcType=VARCHAR}, SORT_NBR = #{ sortNbr,jdbcType=INTEGER}, ORIGIN_FLAG = #{ originFlag,jdbcType=CHAR}, ORIGIN_APP = #{ originApp,jdbcType=VARCHAR}, ACTOBJ_ID = #{ actobjId,jdbcType=BIGINT}, START_TIME = #{ startTime,jdbcType=TIMESTAMP}, OVER_TIME = #{ overTime,jdbcType=TIMESTAMP}, IP = #{ ip,jdbcType=VARCHAR}, EXPLORER = #{ explorer,jdbcType=VARCHAR}, EXPLORER_VER = #{ explorerVer,jdbcType=VARCHAR}, CLIENT = #{ client,jdbcType=VARCHAR}, LANGUAGE = #{ language,jdbcType=VARCHAR}, COUNTRY = #{ country,jdbcType=VARCHAR}, PROVINCE = #{ province,jdbcType=VARCHAR}, CITY = #{ city,jdbcType=VARCHAR}, AREA = #{ area,jdbcType=VARCHAR}, SCREEN_WIDTH = #{ screenWidth,jdbcType=VARCHAR}, SCREEN_HEIGHT = #{ screenHeight,jdbcType=VARCHAR}, MAC = #{ mac,jdbcType=VARCHAR}
WHERE ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:
UPDATE SYS_L_ACTION_TRACK
NOTES = #{item.notes,jdbcType=VARCHAR}, SORT_NBR = #{item.sortNbr,jdbcType=INTEGER}, ORIGIN_FLAG = #{item.originFlag,jdbcType=CHAR}, ORIGIN_APP = #{item.originApp,jdbcType=VARCHAR}, ACTOBJ_ID = #{item.actobjId,jdbcType=BIGINT}, START_TIME = #{item.startTime,jdbcType=TIMESTAMP}, OVER_TIME = #{item.overTime,jdbcType=TIMESTAMP}, IP = #{item.ip,jdbcType=VARCHAR}, EXPLORER = #{item.explorer,jdbcType=VARCHAR}, EXPLORER_VER = #{item.explorerVer,jdbcType=VARCHAR}, CLIENT = #{item.client,jdbcType=VARCHAR}, LANGUAGE = #{item.language,jdbcType=VARCHAR}, COUNTRY = #{item.country,jdbcType=VARCHAR}, PROVINCE = #{item.province,jdbcType=VARCHAR}, CITY = #{item.city,jdbcType=VARCHAR}, AREA = #{item.area,jdbcType=VARCHAR}, SCREEN_WIDTH = #{item.screenWidth,jdbcType=VARCHAR}, SCREEN_HEIGHT = #{item.screenHeight,jdbcType=VARCHAR}, MAC = #{item.mac,jdbcType=VARCHAR}
WHERE ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  SYS_L_ACTION_TRACK
SET
DELETED_FLAG  =  '1'  WHERE ROW_ID = #{item}

mapper.select:
SELECT *
FROM SYS_L_ACTION_TRACK WHERE DELETED_FLAG='0' AND STATUS = '1'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}

SELECT *
FROM SYS_L_ACTION_TRACK
WHERE DELETED_FLAG='0' AND STATUS = '1'  ${condition}
