mapper.update:
UPDATE SYS_L_FOOTINFO 

NOTES = #{ notes,jdbcType=VARCHAR},
FOOT_INFO = #{ footInfo,jdbcType=VARCHAR}

WHERE
ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:

UPDATE SYS_L_FOOTINFO 

NOTES = #{item.notes,jdbcType=VARCHAR},
FOOT_INFO = #{item.footInfo,jdbcType=VARCHAR}

WHERE
ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  SYS_L_FOOTINFO
SET
DELETED_FLAG  =  '1'  WHERE ROW_ID = #{item}

mapper.select:
SELECT *
FROM SYS_L_FOOTINFO WHERE DELETED_FLAG='0' AND STATUS = '1' 
AND FARM_ID=#{ farmId,jdbcType=INTEGER}

SELECT *
FROM SYS_L_FOOTINFO WHERE DELETED_FLAG='0' AND STATUS = '1'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}

SELECT *
FROM SYS_L_FOOTINFO
WHERE DELETED_FLAG='0' AND STATUS = '1'  ${condition}
