mapper.update:
UPDATE SYS_L_PRC_ORDER
STATUS = #{ status,jdbcType=VARCHAR}, ORDER_NO = #{ orderNo,jdbcType=VARCHAR}, ORDER_NAME = #{ orderName,jdbcType=VARCHAR}, IN_PARAM = #{ inParam,jdbcType=VARCHAR}, OPERA_CLASS = #{ operaClass,jdbcType=VARCHAR}, NOTES = #{ notes,jdbcType=VARCHAR}, CREATE_ID = #{ createId,jdbcType=INTEGER}, CREATE_DATE = #{ createDate,jdbcType=TIMESTAMP}, EXECUTE_DATE = #{ executeDate,jdbcType=TIMESTAMP}
WHERE ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:
UPDATE SYS_L_PRC_ORDER
STATUS = #{ item.status,jdbcType=VARCHAR}, ORDER_NO = #{item.orderNo,jdbcType=VARCHAR}, ORDER_NAME = #{item.orderName,jdbcType=VARCHAR}, IN_PARAM = #{item.inParam,jdbcType=VARCHAR}, OPERA_CLASS = #{item.operaClass,jdbcType=VARCHAR}, NOTES = #{item.notes,jdbcType=VARCHAR}, CREATE_ID = #{item.createId,jdbcType=INTEGER}, CREATE_DATE = #{item.createDate,jdbcType=TIMESTAMP}, EXECUTE_DATE = #{item.executeDate,jdbcType=TIMESTAMP}
WHERE ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  SYS_L_PRC_ORDER
SET
DELETED_FLAG  =  '1'  WHERE ROW_ID = #{item}

mapper.select:
SELECT *
FROM SYS_L_PRC_ORDER WHERE DELETED_FLAG='0' AND STATUS = '1' 
AND FARM_ID=#{ farmId,jdbcType=INTEGER}

SELECT *
FROM SYS_L_PRC_ORDER WHERE DELETED_FLAG='0' AND STATUS = '1'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}

SELECT *
FROM SYS_L_PRC_ORDER
WHERE DELETED_FLAG='0' AND STATUS = '1'  ${condition}
